  bottom: 0;
  left: 0;
  margin: auto;
  display: table;
}

/* Basscss UI Utility Button Sizes */

.button-small {
  padding: var(--button-small-padding-y) var(--button-small-padding-x);
}

.button-big {
  padding: var(--button-big-padding-y) var(--button-big-padding-x);
}

.button-narrow {
  padding-left: var(--space-1);
  padding-right: var(--space-1);
}

:root {
  --space-1: .5rem;
  --button-small-padding-y: .25rem;
  --button-small-padding-x: .5rem;
  --button-big-padding-y: 1rem;
  --button-big-padding-x: 1.25rem;
}

/* Basscss Grid */

.container {
  max-width: var(--container-width);
  margin-left: auto;
  margin-right: auto;
}
.col {
  float: left;
  box-sizing: border-box;
}

.col-right {
  float: right;
  box-sizing: border-box;
}

.col-1 {
  width: calc(1/12 * 100%);
}

.col-2 {
  width: calc(2/12 * 100%);
}

.col-3 {
  width: calc(3/12 * 100%);
}

.col-4 {
  width: calc(4/12 * 100%);
}

.col-5 {
  width: calc(5/12 * 100%);
}

.col-6 {
  width: calc(6/12 * 100%);
}

.col-7 {
  width: calc(7/12 * 100%);
}

.col-8 {
  width: calc(8/12 * 100%);
}

.col-9 {
  width: calc(9/12 * 100%);
}

.col-10 {
  width: calc(10/12 * 100%);
}

.col-11 {
  width: calc(11/12 * 100%);
}

.col-12 {
  width: 100%;
}
@media (--breakpoint-sm) {

  .sm-col {
    float: left;
    box-sizing: border-box;
  }

  .sm-col-right {
    float: right;
    box-sizing: border-box;
  }

