.button-link:active,
.button-link.is-active {
  box-shadow: inset 0 -.125em;
}

.button-link:focus {
  outline: none;
  border-color: transparent;
  box-shadow: 0 0 3px, inset 0 -.125em;
}

.button-link:disabled,
.button-link.is-disabled {
  opacity: .5;
}

/* Basscss Button Blue */

.button-blue {
  color: white;
  background-color: var(--blue);
  border-radius: var(--border-radius);
  transition-duration: .1s;
  transition-timing-function: ease-out;
  transition-property: box-shadow, background-color;
}

.button-blue:hover {
  opacity: .875;
}

.button-blue:active,
.button-blue.is-active {
  box-shadow: inset 0 0 0 32px rgba(#000,.125), inset 0 2px 3px 0 rgba(#000,.25);
}

.button-blue:focus {
  outline: none;
  box-shadow: 0 0 0 2px rgba(white, .5), 0 0 1px 4px rgba(var(--blue), .5);
}

.button-blue:disabled,
.button-blue.is-disabled {
  opacity: .5;
}

:root {
  --blue: #0076df;
  --border-radius: 3px;
}

/* Basscss Button Blue Outline */

.button-blue-outline {
  color: var(--blue);
  background-color: transparent;
  border-radius: var(--border-radius);
  border: var(--border-width) solid var(--blue);
  transition-duration: .1s;
  transition-timing-function: ease-out;
  transition-property: box-shadow, background-color;
}

.button-blue-outline:hover,
.button-blue-outline.is-active {
  color: white;
  background-color: var(--blue);
}

.button-blue-outline:active {
  box-shadow: inset 0 3px 3px 0 rgba(#000,.25);
}

.button-blue-outline:focus {
  outline: none;
  box-shadow: 0 0 0 2px rgba(white, .5), 0 0 1px 4px rgba(var(--blue), .5);
}

.button-blue-outline:disabled,
.button-blue-outline.is-disabled {
  color: var(--blue);
  background-color: transparent;
  opacity: .5;
}


:root {
  --blue: #0076df;
  --border-radius: 3px;
  --border-width: 1px;
}

/* Basscss Button Gray */

.button-gray {
  color: white;
  background-color: var(--mid-gray);
  border-radius: var(--border-radius);
  transition-duration: .1s;
  transition-timing-function: ease-out;
