}

.button-gray:focus {
  outline: none;
  box-shadow: 0 0 0 2px white, 0 0 1px 4px rgba(var(--blue), .5);
}

.button-gray:disabled,
.button-gray.is-disabled {
  opacity: .5;
}

:root {
  --mid-gray: #777;
  --blue: #0076df;
  --border-radius: 3px;
}

/* Basscss Button Light Gray */

.button-light-gray {
  color: var(--dark-gray);
  background-color: var(--light-gray);
  border-radius: var(--border-radius);
  transition-duration: .1s;
  transition-timing-function: ease-out;
  transition-property: box-shadow, background-color;
}

.button-light-gray:hover {
  opacity: .875;
}

.button-light-gray:active,
.button-light-gray:is-active {
  box-shadow: inset 0 0 0 32px rgba(#000,.125), inset 0 2px 3px 0 rgba(#000,.25);
}

.button-light-gray:focus {
  outline: none;
  box-shadow: 0 0 0 2px white, 0 0 1px 4px rgba(var(--blue), .5);
}

.button-light-gray:disabled,
.button-light-gray.is-disabled {
  opacity: .5;
}

:root {
  --blue: #0076df;
  --light-gray: #ccc;
  --dark-gray: #333;
  --border-radius: 3px;
}

/* Basscss Button Red */

.button-red {
  color: white;
  background-color: var(--red);
  border-radius: var(--border-radius);
  transition-duration: .1s;
  transition-timing-function: ease-out;
  transition-property: box-shadow, background-color;
}

.button-red:hover {
  opacity: .875;
}

.button-red:active,
.button-red.is-active {
  box-shadow: inset 0 0 0 32px rgba(#000,.125), inset 0 2px 3px 0 rgba(#000,.25);
}

.button-red:focus {
  outline: none;
  box-shadow: 0 0 0 2px rgba(white, .5), 0 0 1px 4px rgba(var(--red), .5);
}

.button-red:disabled,
.button-red.is-disabled {
  opacity: .5;
}

:root {
  --red: #f95020;
  --border-radius: 3px;
}

/* Basscss Button Nav Light */

.button-nav-light {
  color: var(--button-nav-light-color);
  background-color: transparent;
}

.button-nav-light:hover {
  background-color: var(--hover-background-color);
}
.button-nav-light:active,
.button-nav-light.is-active {
  color: var(--dark-gray);
}

:root {
  --dark-gray: #333;
  --darken-1: rgba(#000,.0625);
  --button-nav-light-color: var(--blue, inherit);
  --hover-background-color: var(--darken-1);
  --border-radius: 3px;
}

/* Basscss Button Nav Dark */

.button-nav-dark {
  color: white;
  background-color: transparent;
}

.button-nav-dark:hover {
  background-color: var(--darken-2);
}

.button-nav-dark:active,
.button-nav-dark.is-active {
  background-color: var(--darken-2);
}

:root {
  --darken-2: rgba(#000,.125);
}

/* Basscss Button Nav Tab */

.button-nav-tab {
  margin-bottom: -1px;
  border-radius: var(--border-radius) var(--border-radius) 0 0;
  border-bottom: 1px solid var(--border-color);
  color: var(--dark-gray);
  background-color: transparent;
}

.button-nav-tab:hover {
  background-color: var(--hover-background-color);
}

.button-nav-tab.is-active {
  background-color: white;
  border-bottom: 1px solid white;
  border-top: 1px solid var(--border-color);
  border-left: 1px solid var(--border-color);
  border-right: 1px solid var(--border-color);
}

:root {
  --dark-gray: #333;
  --light-gray: #ccc;
  --darken-1: rgba(#000,.0625);
  --hover-background-color: var(--darken-1);
  --border-color: var(--light-gray);
  --border-radius: 3px;
}

/* Basscss Highlight */

.hljs {
  -webkit-text-size-adjust: none;
}

.hljs-comment,
.diff .hljs-header,
.hljs-javadoc {
  color: var(--hljs-comment);
  font-style: italic;
}

.hljs-keyword,
.css .rule .hljs-keyword,
.hljs-winutils,
.nginx .hljs-title,
.hljs-subst,
.hljs-request,
.hljs-status {
  color: var(--hljs-keyword);
  font-weight: bold;
}

.hljs-number,
.hljs-hexcolor,
.ruby .hljs-constant {
  color: var(--hljs-number);
}

.hljs-string,
.hljs-tag .hljs-value,
.hljs-phpdoc,
.hljs-dartdoc,
.tex .hljs-formula {
  color: var(--hljs-string);
}

.hljs-title,
.hljs-id,
.scss .hljs-preprocessor {
  color: var(--hljs-title);
  font-weight: bold;
}

.hljs-list .hljs-keyword,
.hljs-subst {
  font-weight: normal;
}

.hljs-class .hljs-title,
.hljs-type,
.vhdl .hljs-literal,
.tex .hljs-command {
  color: var(--hljs-type);
  font-weight: bold;
}

.hljs-tag,
.hljs-tag .hljs-title,
.hljs-rules .hljs-property,
.django .hljs-tag .hljs-keyword {
  color: var(--hljs-tag);
  font-weight: normal;
}

.hljs-attribute,
.hljs-variable,
.lisp .hljs-body {
  color: var(--hljs-attribute);
}

.hljs-regexp {
  color: var(--hljs-regexp);
}

.hljs-symbol,
.ruby .hljs-symbol .hljs-string,
.lisp .hljs-keyword,
.clojure .hljs-keyword,
.scheme .hljs-keyword,
.tex .hljs-special,
.hljs-prompt {
  color: var(--hljs-symbol);
}

.hljs-built_in {
  color: var(--hljs-built-in);
}

.hljs-preprocessor,
.hljs-pragma,
.hljs-pi,
.hljs-doctype,
.hljs-shebang,
.hljs-cdata {
  color: var(--hljs-preprocessor);
  font-weight: bold;
}

.hljs-deletion {
  background: var(--hljs-deletion);
}

.hljs-addition {
  background: var(--hljs-addition);
}

.diff .hljs-change {
  background: var(--hljs-change);
}

.hljs-chunk {
  color: var(--hljs-chunk);
}


:root {
  --monospace-font-family: 'Source Code Pro', Consolas, monospace;
  --blue: #0076df;
  --red: #f95020;
  --green: #00cf26;
  --yellow: #efcc00;
  --dark-gray: #333;
  --mid-gray: #777;
  --light-gray: #ccc;

  --purple: #f92080;
  --dark-blue: #00369f;
  --dark-green: #00ab37;
  --dark-green: #009f06;
  --light-pink: #ffdddd;
  --light-green: #ddffdd;

  /* Mapped colors */
  --hljs-comment: var(--mid-gray);
  --hljs-keyword: var(--dark-gray);
  --hljs-number: var(--dark-green);
  --hljs-string: var(--red);
  --hljs-title: var(--red);
  --hljs-type: var(--dark-blue);
  --hljs-tag: var(--dark-blue);
  --hljs-attribute: var(--dark-green);
  --hljs-regexp: var(--dark-green);
  --hljs-symbol: var(--purple);
  --hljs-built-in: var(--dark-blue);
  --hljs-preprocessor: var(--mid-gray);
  --hljs-deletion: var(--light-pink);
  --hljs-addition: var(--light-green);
  --hljs-change: var(--dark-blue);
  --hljs-chunk: var(--light-gray);
}

